import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const links = [
  { path: '/', label: 'Home' },
  { path: '/dashboard', label: 'Dashboard' },
  { path: '/moviewatchlist', label: 'Watchlist' },
  { path: '/profile', label: 'Profile' },
  { path: '/add-movie', label: 'Add Movie' },
  { path: '/edit-movie', label: 'Edit Movie' },
  { path: '/movie-details', label: 'Movie Details' },
  { path: '/admin', label: 'Admin Dashboard' },
  { path: '/search', label: 'Search' },
];

function Header() {
  const location = useLocation();
  const navigate = useNavigate();

  // Manage authentication state (e.g., use context or Redux for real app)
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogout = () => {
    setIsAuthenticated(false); // Change state to simulate logging out
    navigate('/'); // Redirect to home page after logout
  };

  const renderNavLink = (path, label) => (
    <li className="nav-item" key={path}>
      <Link to={path} className={`nav-link ${location.pathname === path ? 'text-danger' : 'text-warning'}`}>
        {label}
      </Link>
    </li>
  );

  return (
    <header className="bg-dark py-3">
      <div className="container d-flex justify-content-between align-items-center">
        {/* Main navigation links */}
        <nav className="navbar navbar-expand-lg navbar-dark">
          <ul className="navbar-nav mr-auto d-flex gap-3">
            {links.map(({ path, label }) => renderNavLink(path, label))}
          </ul>
        </nav>

        {/* Authentication dropdown */}
        <div className="dropdown">
          <button
            className="btn btn-secondary dropdown-toggle"
            type="button"
            id="authDropdown"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Account
          </button>
          <ul className="dropdown-menu" aria-labelledby="authDropdown">
            {isAuthenticated ? (
              <>
                <li>
                  <Link to="/profile" className="dropdown-item">Profile</Link>
                </li>
                <li>
                  <button className="dropdown-item" onClick={handleLogout}>Logout</button>
                </li>
              </>
            ) : (
              <>
                <li>
                  <Link to="/login" className="dropdown-item">Login</Link>
                </li>
                <li>
                  <Link to="/signup" className="dropdown-item">Sign Up</Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </header>
  );
}

export default Header;
