import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

function Movies() {
  const [movies, setMovies] = useState([]);
  const defaultPoster = "https://via.placeholder.com/150"; // Default image for missing posters

  // Fetch movies from API when the component mounts
  useEffect(() => {
    fetch('http://group16-project2-07b42fe3d45e.herokuapp.com/Movies/')
      .then(response => response.json())
      .then(data => {
        console.log("Fetched Movies Data:", data);  // Log the fetched data
        setMovies(data);           // Store movies in state
      })
      .catch(error => console.error('Error fetching movies:', error));
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4 text-warning">Movies List</h1>

      {/* Movie Cards */}
      <div className="row">
        {movies.length > 0 ? (
          movies.map(movie => (
            <div key={movie.movie_id} className="col-sm-6 col-md-4 col-lg-3 mb-4">
              <div className="card bg-dark text-white" style={{ maxWidth: '180px', margin: '0 auto' }}>
                <img
                  src={movie.poster || defaultPoster}  // Use default poster if none is provided
                  alt={movie.title}
                  className="card-img-top"
                  style={{ height: '250px', objectFit: 'cover' }}
                />
                <div className="card-body">
                  <h6 className="card-title">{movie.title}</h6>
                  <p className="card-text"><small><strong>Director:</strong> {movie.director}</small></p>
                  <p className="card-text"><small><strong>Year:</strong> {movie.year}</small></p>
                  <p className="card-text"><small><strong>Genre:</strong> {movie.genre}</small></p>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-white">No movies found.</p>
        )}
      </div>
    </div>
  );
}

export default Movies;
