import React from 'react';
import Header from './Header'; // Assuming you have a Header component
import Footer from './Footer'; // Assuming you have a Footer component

function Layout({ children }) {
  const layoutStyle = {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh', // Ensure the layout takes up the entire viewport height
  };

  const contentStyle = {
    flexGrow: 1, // Ensures the content takes up all available space between header and footer
    padding: '20px', // Add some padding to the content area
    display: 'flex',
    justifyContent: 'center', // Center horizontally
    // alignItems: 'center', // Center vertically
  };

  return (
    <div style={layoutStyle}>
      {/* Header */}
      <Header />

      {/* Main Content Area */}
      <main style={contentStyle}>
        {children}
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default Layout;
