import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout'; // Adjust the path to your Layout component

function AdminDashboard() {
  const [users, setUsers] = useState([
    { id: 1, name: 'Luis Ruiz', email: 'luis.ruiz@example.com' },
    { id: 2, name: 'Jane Doe', email: 'jane.doe@example.com' },
    { id: 3, name: 'John Smith', email: 'john.smith@example.com' }
  ]);

  // Delete user by ID
  const deleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  return (
    <Layout>
      <h1>Admin Dashboard</h1>
      <p>Manage users, movies, and wishlists.</p>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => deleteUser(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Layout>
  );
}

export default AdminDashboard;
