import React from 'react';
import Header from './Header.js';  // Import the header
import Footer from './Footer.js';  // Import the footer
import 'bootstrap/dist/css/bootstrap.min.css';  // Import Bootstrap

const layoutStyle = {
  minHeight: '100vh',  // Ensure the layout covers the full viewport
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',  // Ensure header is at the top, footer at the bottom
  backgroundColor: '#1a1a1a',  // IMDb dark gray background
  color: '#FFFFFF',  // White text for contrast
};

const mainStyle = {
  flex: '1',  // Take up the remaining space between header and footer
  maxWidth: '1200px',   // Consistent max width for all pages
  width: '100%',        // Ensure full width within the max width
  padding: '20px',      // Uniform padding for all pages
  margin: '20px auto',  // Center content horizontally
  backgroundColor: '#ffffff',  // White background for content
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',  // Slightly stronger shadow for depth
  borderRadius: '8px',  // Rounded corners
  color: '#1a1a1a',  // Dark text for readability on white background
};

const footerStyle = {
  padding: '20px',
  backgroundColor: '#1a1a1a',  // Match footer to layout background
  color: '#FFFFFF',  // White text in the footer
  textAlign: 'center',
  width: '100%',
};

// Styles for forms using Bootstrap with IMDb theme colors
const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  width: '100%',
};

const inputStyle = {
  padding: '10px',
  margin: '10px 0',
  width: '100%',
  maxWidth: '400px',  // Maximum width for the input fields
  borderRadius: '4px',
  border: '1px solid #ccc',
  fontSize: '16px',
};

const buttonStyle = {
  padding: '10px 20px',
  backgroundColor: '#F5C518',  // IMDb yellow
  color: '#1a1a1a',  // Dark text on yellow background
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '16px',
};

function Layout({ children }) {
  return (
    <div style={layoutStyle}>
      <Header />  {/* Reusable Header */}
      <main style={mainStyle}>
        {children} {/* Page-specific content goes here */}
      </main>
      <footer style={footerStyle}>
        <Footer />  {/* Reusable Footer */}
      </footer>
    </div>
  );
}

export default Layout;

export { formStyle, inputStyle, buttonStyle };
