import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout'; // Adjust the path to your Layout component

function AdminDashboard() {
  const [users, setUsers] = useState([]);

  // Fetch users from the Django backend when the component loads
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/users/'); // Adjust the URL to your Django API endpoint
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  // Delete user by ID
  const deleteUser = async (id) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/${id}/`, {
        method: 'DELETE',
      });
      if (response.status === 204) {
        // If deletion was successful, remove the user from the state
        setUsers(users.filter(user => user.id !== id));
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <Layout>
      <h1>Admin Dashboard</h1>
      <p>Manage users, movies, and wishlists.</p>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => deleteUser(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Layout>
  );
}

export default AdminDashboard;
