import React, { useState } from 'react';
import Layout from '../components/Layout';
import './styles/AddMovie.css';  // Import the CSS file

function AddMovie() {
  const [movieTitle, setMovieTitle] = useState('');
  const [directors, setDirectors] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [description, setDescription] = useState('');
  const [genre, setGenre] = useState('');

  const handleAddMovie = (e) => {
    e.preventDefault();
    const newMovie = {
      title: movieTitle,
      directors,
      image: imageUrl,
      description,
      genre,
    };
    console.log("Movie Added:", newMovie);
    // You can send `newMovie` to the backend or handle it further here
  };

  return (
    <Layout>
      <div className="container">
        <h1 className="header">Add Movie to Movie List</h1>
        <form onSubmit={handleAddMovie} className="form">
          <div className="formGroup">
            <label className="label">Movie Title</label>
            <input 
              type="text" 
              value={movieTitle} 
              onChange={(e) => setMovieTitle(e.target.value)} 
              required 
              className="input"
            />
          </div>
          <div className="formGroup">
            <label className="label">Directors</label>
            <input 
              type="text" 
              value={directors} 
              onChange={(e) => setDirectors(e.target.value)} 
              required 
              className="input"
            />
          </div>
          <div className="formGroup">
            <label className="label">Image URL</label>
            <input 
              type="text" 
              value={imageUrl} 
              onChange={(e) => setImageUrl(e.target.value)} 
              required 
              className="input"
            />
          </div>
          <div className="formGroup">
            <label className="label">Description</label>
            <textarea 
              value={description} 
              onChange={(e) => setDescription(e.target.value)} 
              required 
              className="input"
            />
          </div>
          <div className="formGroup">
            <label className="label">Genre</label>
            <input 
              type="text" 
              value={genre} 
              onChange={(e) => setGenre(e.target.value)} 
              required 
              className="input"
            />
          </div>
          <button type="submit" className="updateButton">Add Movie</button>
        </form>
      </div>
    </Layout>
  );
}

export default AddMovie;
