import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import 'bootstrap/dist/css/bootstrap.min.css';  // Import Bootstrap

function LogoutConfirmation() {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/'); // Redirect to home page after 3 seconds
    }, 3000);

    return () => clearTimeout(timer); // Cleanup the timer on component unmount
  }, [navigate]);

  return (
    <Layout>
      <div className="container d-flex justify-content-center align-items-center" style={styles.fullHeight}>
        <div className="text-center p-5" style={styles.transparentBox}>
          <h1 className="text-white mb-4">Logout Successful</h1>
          <p className="text-white-50">You have successfully logged out. Redirecting to the homepage...</p>
        </div>
      </div>
    </Layout>
  );
}

const styles = {
  fullHeight: {
    minHeight: '50vh',  // Ensure the content takes up the full viewport height
  },
  transparentBox: {
    backgroundColor: 'rgba(0, 0, 0, 0.6)',  // Dark transparent background
    borderRadius: '8px',  // Rounded corners
    padding: '20px',  // Extra padding for better spacing
  }
};

export default LogoutConfirmation;
