import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import axios from 'axios';
import LoginButton from '../components/login';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  // Handle form submission for login
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://your-api-url.com/login', {
        username: username,
        password: password,
      });

      // If login is successful, navigate to the dashboard
      if (response.data.success) {
        navigate('/dashboard'); // Redirect to dashboard
      } else {
        setErrorMessage('Invalid login credentials'); // Display error message
        setPassword(''); // Clear the password field
      }
    } catch (error) {
      setErrorMessage('An error occurred. Please try again.'); // Handle API errors
      setPassword(''); // Clear the password field
    }
  };

  return (
    <Layout>
      <div className="container mt-5 d-flex justify-content-center">
        <div className="col-md-6 bg-dark p-4 rounded shadow-sm">
          <h1 className="text-center mb-4 text-warning">Login</h1>
          {/* Display error message if present */}
          {errorMessage && (
            <div className="alert alert-danger text-center" role="alert">
              {errorMessage}
            </div>
          )}
          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label text-light">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="form-control bg-dark text-white border-light"
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label text-light">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control bg-dark text-white border-light"
                required
              />
            </div>
            <button type="submit" className="btn btn-warning w-100">Login</button>
          </form>
          <div className="mt-3 text-center">
            <LoginButton />
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Login;
