import React from 'react';
import { Link } from 'react-router-dom';
import Layout from '../components/Layout';
import 'bootstrap/dist/css/bootstrap.min.css';
import LoginButton from '../components/login';

const homeStyle = {
  backgroundColor: '#1a1a1a', // IMDb dark background
  color: '#FFFFFF',           // White text
  padding: '40px',
  borderRadius: '12px',
  textAlign: 'center',
  maxWidth: '800px',
  margin: '0 auto',          // Center the content
  boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
};

const linkStyle = {
  color: '#F5C518',           // IMDb yellow for links
  textDecoration: 'none',
  fontWeight: 'bold',
  display: 'block',
  marginTop: '20px',
};

const footerStyle = {
  padding: '20px',
  backgroundColor: '#1a1a1a',
  color: '#FFFFFF',
  textAlign: 'center',
};

function Home() {
  return (
    <Layout>
      <div style={homeStyle} className="container-lg">
        <h1 className="mb-4">Welcome to the Home Page</h1>
        <p className="mb-4">This is the home page of the application.</p>

        {/* Add a link to the login page */}
        <Link to="/login" style={linkStyle}>Go to Login</Link>

        {/* Optional: Add the login button */}
        <div className="mt-4">
          <LoginButton />
        </div>
      </div>
      <footer style={footerStyle}>
        Movie Wishlist Â© 2024
      </footer>
    </Layout>
  );
}

export default Home;
