import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

const links = [
  { path: '/', label: 'Home' },
  { path: '/dashboard', label: 'Dashboard' },
  { path: '/moviewatchlist', label: 'Watchlist' },
  { path: '/profile', label: 'Profile' },
  { path: '/add-movie', label: 'Add Movie' },
  { path: '/edit-movie', label: 'Edit Movie' },
  { path: '/movie-details', label: 'Movie Details' },
  { path: '/admin', label: 'Admin Dashboard' },
  { path: '/search', label: 'Search' },
];

const authLinks = [
  { path: '/login', label: 'Login' },
  { path: '/signup', label: 'Sign Up' },
  { path: '/logout', label: 'Logout' },
];

const customStyles = {
  header: {
    backgroundColor: '#1a1a1a', // IMDb dark background color
  },
  navLink: {
    color: '#FFD700', // IMDb Gold for normal links
  },
  activeLink: {
    color: '#FF6347', // Red color for active links
  },
};

function Header() {
  const location = useLocation(); // Get the current location

  // Function to generate list items for navigation
  const renderNavLink = (path, label) => (
    <li className="nav-item" key={path}>
      <Link
        to={path}
        className={`nav-link ${location.pathname === path ? 'text-danger' : 'text-warning'}`} // Using Bootstrap text classes
      >
        {label}
      </Link>
    </li>
  );

  return (
    <header style={customStyles.header} className="py-3">
      <div className="container d-flex justify-content-between align-items-center">
        {/* Main navigation links */}
        <nav className="flex-grow-1">
          <ul className="nav justify-content-center">
            {links.map(({ path, label }) => renderNavLink(path, label))}
          </ul>
        </nav>

        {/* Authentication-related links (aligned to the right) */}
        <div className="d-flex gap-3">
          {authLinks.map(({ path, label }) => renderNavLink(path, label))}
        </div>
      </div>
    </header>
  );
}

export default Header;
