import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const links = [
  { path: '/', label: 'Home' },
  { path: '/dashboard', label: 'Dashboard' },
  { path: '/moviewatchlist', label: 'Watchlist' },
  { path: '/profile', label: 'Profile' },
  { path: '/add-movie', label: 'Add Movie' },
  { path: '/edit-movie', label: 'Edit Movie' },
  { path: '/movie-details', label: 'Movie Details' },
  { path: '/admin', label: 'Admin Dashboard' },
  { path: '/search', label: 'Search' },
];

const authLinks = [
  { path: '/login', label: 'Login' },
  { path: '/signup', label: 'Sign Up' },
  { path: '/logout', label: 'Logout' },
];

function Header() {
  const location = useLocation();

  const renderNavLink = (path, label) => (
    <li className="nav-item" key={path}>
      <Link to={path} className={`nav-link ${location.pathname === path ? 'text-danger' : 'text-warning'}`}>
        {label}
      </Link>
    </li>
  );

  return (
    <header className="bg-dark py-3">
      <div className="container d-flex justify-content-between align-items-center">
        {/* Main navigation links */}
        <nav className="navbar navbar-expand-lg navbar-dark">
          <ul className="navbar-nav mr-auto d-flex gap-3">
            {links.map(({ path, label }) => renderNavLink(path, label))}
          </ul>
        </nav>

        {/* Authentication dropdown */}
        <div className="dropdown">
          <button
            className="btn btn-secondary dropdown-toggle "
            type="button"
            id="authDropdown"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Account
          </button>
          <ul className="dropdown-menu" aria-labelledby="authDropdown">
            {authLinks.map(({ path, label }) => (
              <li key={path}>
                <Link to={path} className="dropdown-item">{label}</Link>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </header>
  );
}

export default Header;
