import React, { useState } from 'react';
import Layout from '../components/Layout';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap is imported

function MovieWatchlist() {
  // Simulated user movies and all available movies
  const [userMovies, setUserMovies] = useState([
    { id: 1, title: "The Matrix", status: "Favorite", watchlist_id: 1001 },
    { id: 2, title: "Inception", status: "Planned", watchlist_id: 1002 },
    { id: 3, title: "The Dark Knight", status: "Watched", watchlist_id: 1003 },
  ]);

  const allMovies = [
    { movieId: 1, title: "The Matrix" },
    { movieId: 2, title: "Inception" },
    { movieId: 3, title: "The Dark Knight" },
    { movieId: 4, title: "Pulp Fiction" },
    { movieId: 5, title: "Interstellar" }
  ];

  // Handle updating the movie details
  const handleUpdate = (e, watchlistId) => {
    e.preventDefault();
    console.log("Movie updated for watchlistId:", watchlistId);
  };

  // Handle deleting the movie from the watchlist
  const handleDelete = (e, watchlistId) => {
    e.preventDefault();
    setUserMovies(userMovies.filter(movie => movie.watchlist_id !== watchlistId));
    console.log("Movie deleted from watchlistId:", watchlistId);
  };

  return (
    <Layout>
      <div className="container mt-5">
        <h2 className="mb-4">Update Your Favorite Movies</h2>

        {userMovies && userMovies.length > 0 ? (
          userMovies.map((movie) => (
            <form key={movie.watchlist_id} onSubmit={(e) => handleUpdate(e, movie.watchlist_id)} className="mb-4">
              <div className="form-group row">
                <label className="col-sm-2 col-form-label font-weight-bold">Title (Current):</label>
                <div className="col-sm-10">
                  <p className="form-control-plaintext">{movie.title}</p>
                </div>
              </div>

              <div className="form-group row">
                <label htmlFor="movieId" className="col-sm-2 col-form-label">Change Movie:</label>
                <div className="col-sm-10">
                  <select name="movieId" className="form-control" defaultValue={movie.id}>
                    {allMovies.map((m) => (
                      <option key={m.movieId} value={m.movieId}>
                        {m.title}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group row">
                <label htmlFor="status" className="col-sm-2 col-form-label">Status:</label>
                <div className="col-sm-10">
                  <select name="status" className="form-control" defaultValue={movie.status}>
                    <option value="Planned">Planned</option>
                    <option value="Watched">Watched</option>
                    <option value="Favorite">Favorite</option>
                  </select>
                </div>
              </div>

              <div className="form-group row">
                <div className="col-sm-10 offset-sm-2">
                  <button type="submit" className="btn btn-primary mr-2">Update Movie</button>
                  <button type="button" className="btn btn-danger" onClick={(e) => handleDelete(e, movie.watchlist_id)}>
                    Delete
                  </button>
                </div>
              </div>
            </form>
          ))
        ) : (
          <p>No movies found in your watchlist.</p>
        )}
      </div>
    </Layout>
  );
}

export default MovieWatchlist;
