import React, { useState } from 'react';
import Layout from '../components/Layout';

function Dashboard() {
  // Simulated watchlist data
  const [watchlist, setWatchlist] = useState([
    { id: 1, title: 'The Matrix', year: 1999, director: 'The Wachowskis' },
    { id: 2, title: 'Inception', year: 2010, director: 'Christopher Nolan' },
  ]);

  return (
    <Layout>
      <h1>Dashboard</h1>
      <p>Welcome to your Dashboard. Here you can manage your movie wishlist, profile, and more.</p>

      {/* Section for displaying the watchlist */}
      <section>
        <h2>Your Watchlist</h2>
        {watchlist.length > 0 ? (
          <ul>
            {watchlist.map(movie => (
              <li key={movie.id}>
                <h3>{movie.title}</h3>
                <p>Year: {movie.year}</p>
                <p>Director: {movie.director}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No movies in your watchlist yet.</p>
        )}
      </section>

      {/* Placeholder for profile management */}
      <section>
        <h2>Profile Management</h2>
        <p>Manage your account details, update your profile information, and change your password.</p>
        <button style={styles.button}>Update Profile</button>
      </section>

      {/* Placeholder for notifications or reminders */}
      <section>
        <h2>Notifications</h2>
        <p>You currently have no notifications. Keep track of movie releases or reminders here.</p>
      </section>
    </Layout>
  );
}

const styles = {
  button: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
  },
};

export default Dashboard;
