import React from 'react';
import Header from './Header.js';  // Import the header
import Footer from './Footer.js';  // Import the footer

const layoutStyle = {
  minHeight: '100vh',  // Ensures the layout covers the full viewport height
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',  // Center content horizontally
  justifyContent: 'space-between',  // Pushes Header to top, Footer to bottom
  backgroundColor: '#f4f4f9',  // Light background for the layout
};

const contentStyle = {
  flex: '1',
  display: 'flex',
  flexDirection: 'column',  // Ensures vertical stacking inside the content
  justifyContent: 'center',  // Centers content vertically inside main
  alignItems: 'center',  // Centers content horizontally
  padding: '20px',
  maxWidth: '800px',  // Restrict width for better readability
  width: '100%',  // Ensure the content takes up the available width
  backgroundColor: '#fff',  // White background for content area
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',  // Adds subtle shadow for depth
  borderRadius: '8px',  // Slightly rounded corners
  textAlign: 'center',  // Centers text content
};

function Layout({ children }) {
  return (
    <div style={layoutStyle}>
      <Header />  {/* Reusable Header */}
      <main style={contentStyle}>
        {children} {/* Page-specific content goes here */}
      </main>
      <Footer />  {/* Reusable Footer */}
    </div>
  );
}

export default Layout;
