import React from 'react';
import Header from './Header.js';  // Import the header
import Footer from './Footer.js';  // Import the footer

const layoutStyle = {
  minHeight: '100vh', // Ensure the layout covers the full viewport
  display: 'flex',
  flexDirection: 'column',



  alignItems: 'center', // Center content horizontally
   // Center content vertically
  backgroundColor: '#f4f4f9'
};

const contentStyle = {
  flex: '1', // Pushes the footer to the bottom
  padding: '20px',
};
const styles = {
  container: {
    justifyContent: 'center',
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    backgroundColor: '#fff',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  formGroup: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    padding: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '16px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginBottom: '20px',
  },
  addButton: {
    padding: '10px 15px',
    marginBottom: '20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  deleteButton: {
    padding: '5px 5px',
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  editButton: {
    padding: '5px 10px',
    marginRight: '10px',
    backgroundColor: '#2196F3',
    color: 'white',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  th: {
    backgroundColor: '#f2f2f2',
    textAlign: 'left',
    padding: '8px',
  },
  td: {
    padding: '8px',
    textAlign: 'left',
  },
};


function Layout({ children }) {
  return (
    <div style={layoutStyle}>
      <Header />  {/* Reusable Header */}
      <main style={contentStyle}>
        {children} {/* Page-specific content goes here */}
      </main>
      <Footer />  {/* Reusable Footer */}
    </div>
  );
}

export default Layout;
