import React from 'react';
import Header from './Header.js';  // Import the header
import Footer from './Footer.js';  // Import the footer

const layoutStyle = {
  minHeight: '100vh', // Ensure the layout covers the full viewport
  display: 'flex',
  flexDirection: 'column',



  alignItems: 'center', // Center content horizontally
   // Center content vertically
  backgroundColor: '#f4f4f9'
};

const contentStyle = {
  display:'flex',
  padding: '20px',
  maxWidth: '800px', // Restrict width for better readability
  margin: '0 auto', // Center content horizontally
  backgroundColor: '#fff', // White background for content area
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Adds subtle shadow for depth
  borderRadius: '8px', // Rounds the corners slightly
  textAlign: 'center', 
};


function Layout({ children }) {
  return (
    <div style={layoutStyle}>
      <Header />  {/* Reusable Header */}
      <main style={contentStyle}>
        {children} {/* Page-specific content goes here */}
      </main>
      <Footer />  {/* Reusable Footer */}
    </div>
  );
}

export default Layout;
