import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const headerStyle = {
  backgroundColor: '#2c2c2c',
  padding: '10px',
};

const ulStyle = {
  listStyleType: 'none',
  margin: 0,
  padding: 0,
  display: 'flex',
  justifyContent: 'space-around',
};

const liStyle = {
  display: 'inline',
};

const linkStyle = {
  color: '#FFD700', // Gold color for links
  textDecoration: 'none',
  fontSize: '18px',
  fontWeight: 'bold',
};

const activeLinkStyle = {
  color: '#FF6347', // Active link highlighted in red
};

function Header() {
  const location = useLocation();

  return (
    <header style={headerStyle}>
      <ul style={ulStyle}>
        <li style={liStyle}>
          <Link to="/" style={location.pathname === '/' ? activeLinkStyle : linkStyle}>Home</Link>
        </li>
        <li style={liStyle}>
          <Link to="/dashboard" style={location.pathname === '/dashboard' ? activeLinkStyle : linkStyle}>Dashboard</Link>
        </li>
        <li style={liStyle}>
          <Link to="/login" style={location.pathname === '/login' ? activeLinkStyle : linkStyle}>Login</Link>
        </li>

      </ul>
    </header>
  );
}

export default Header;
