import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const links = [
  { path: '/', label: 'Home' },
  { path: '/dashboard', label: 'Dashboard' },
  { path: '/moviewatchlist', label: 'Watchlist' },
  { path: '/profile', label: 'Profile' },
  { path: '/add-movie', label: 'Add Movie' },
  { path: '/edit-movie', label: 'Edit Movie' },
  { path: '/movie-details', label: 'Movie Details' },
  { path: '/admin', label: 'Admin Dashboard' },
  { path: '/search', label: 'Search' },
];

const authLinks = [
  { path: '/login', label: 'Login' },
  { path: '/signup', label: 'Sign Up' },
  { path: '/logout', label: 'Logout' },
];

const customStyles = {
  header: {
    backgroundColor: '#1a1a1a', // IMDb dark background
  },
};

function Header() {
  const location = useLocation(); // Get the current location

  const renderNavLink = (path, label) => (
    <li className="nav-item" key={path}>
      <Link
        to={path}
        className={`nav-link ${location.pathname === path ? 'text-danger' : 'text-warning'}`}
      >
        {label}
      </Link>
    </li>
  );

  return (
    <header style={customStyles.header}>
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container">
          <Link className="navbar-brand text-warning" to="/">Movie Wishlist</Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNavDropdown"
            aria-controls="navbarNavDropdown"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavDropdown">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              {links.map(({ path, label }) => renderNavLink(path, label))}
            </ul>
            <ul className="navbar-nav">
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle text-warning"
                  href="#"
                  id="navbarDropdown"
                  role="button"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Account
                </a>
                <ul className="dropdown-menu dropdown-menu-dark" aria-labelledby="navbarDropdown">
                  {authLinks.map(({ path, label }) => (
                    <li key={path}>
                      <Link to={path} className="dropdown-item">
                        {label}
                      </Link>
                    </li>
                  ))}
                </ul>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}

export default Header;
