import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import Layout from '../components/Layout';

function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [movies, setMovies] = useState([
    {
      id: 1,
      title: "The Matrix",
      genre: "Sci-Fi",
      director: "The Wachowskis",
      year: 1999,
      poster: "https://image.tmdb.org/t/p/w500/qmDpIHrmpJINaRKAfWQfftjCdyi.jpg"
    },
    {
      id: 2,
      title: "Inception",
      genre: "Sci-Fi",
      director: "Christopher Nolan",
      year: 2010,
      poster: "https://image.tmdb.org/t/p/w500/8h58yUsYgnqIR9pvKoMpTl5Lhf7.jpg"
    },
    {
      id: 3,
      title: "The Dark Knight",
      genre: "Action",
      director: "Christopher Nolan",
      year: 2008,
      poster: "https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg"
    },
    {
      id: 4,
      title: "Pulp Fiction",
      genre: "Crime",
      director: "Quentin Tarantino",
      year: 1994,
      poster: "https://image.tmdb.org/t/p/w500/dM2w364MScsjFf8pfMbaWUcWrR.jpg"
    },
    {
      id: 5,
      title: "Interstellar",
      genre: "Sci-Fi",
      director: "Christopher Nolan",
      year: 2014,
      poster: "https://image.tmdb.org/t/p/w500/rAiYTfKGqDCRIIqoNMq8NpZaCk9.jpg"
    }
  ]);

  const [filteredMovies, setFilteredMovies] = useState([]);
  const history = useHistory();

  const handleSearch = (e) => {
    e.preventDefault();
    const results = movies.filter(movie =>
      movie.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      movie.genre.toLowerCase().includes(searchTerm.toLowerCase()) ||
      movie.director.toLowerCase().includes(searchTerm.toLowerCase()) ||
      movie.year.toString().includes(searchTerm)
    );
    setFilteredMovies(results);
  };

  const redirectToMovieInfo = (id) => {
    history.push(`/movie/${id}`); // Redirect to movie info page with the movie ID
  };

  return (
    <Layout>
      <h1>Search Movies</h1>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search by title, genre, director, or year..."
        />
        <button type="submit">Search</button>
      </form>

      <div>
        {filteredMovies.length > 0 ? (
          <ul>
            {filteredMovies.map(movie => (
              <li key={movie.id} onClick={() => redirectToMovieInfo(movie.id)} style={{ cursor: 'pointer' }}>
                <h3>{movie.title}</h3>
                <p>Director: {movie.director}</p>
                <p>Year: {movie.year}</p>
                <p>Genre: {movie.genre}</p>
                <img src={movie.poster} alt={movie.title} style={{ width: '150px' }} />
              </li>
            ))}
          </ul>
        ) : (
          <p>No movies found.</p>
        )}
      </div>
    </Layout>
  );
}

export default Search;
