import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';

function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [movies, setMovies] = useState([
    {
      id: 1,
      title: "The Matrix",
      genre: "Sci-Fi",
      director: "The Wachowskis",
      year: 1999,
      poster: "https://matrix.fandom.com/wiki/The_Matrix?file=The_Matrix_digital_release_cover.jpg"
    },
    {
      id: 2,
      title: "Inception",
      genre: "Sci-Fi",
      director: "Christopher Nolan",
      year: 2010,
      poster: "https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcRRqhsSA3e6Kw-EAbjneujrnhXKq7Ugl5L5Wh0yyLC92qimzfJw"
    },
    {
      id: 3,
      title: "The Dark Knight",
      genre: "Action",
      director: "Christopher Nolan",
      year: 2008,
      poster: "https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg"
    },
    {
      id: 4,
      title: "Pulp Fiction",
      genre: "Crime",
      director: "Quentin Tarantino",
      year: 1994,
      poster: "https://image.tmdb.org/t/p/w500/dM2w364MScsjFf8pfMbaWUcWrR.jpg"
    },
    {
      id: 5,
      title: "Interstellar",
      genre: "Sci-Fi",
      director: "Christopher Nolan",
      year: 2014,
      poster: "https://image.tmdb.org/t/p/w500/rAiYTfKGqDCRIIqoNMq8NpZaCk9.jpg"
    }
  ]);

  const [filteredMovies, setFilteredMovies] = useState(movies);
  const [selectedGenre, setSelectedGenre] = useState(''); // Store selected genre
  const navigate = useNavigate();

  // Filter movies by search term
  const handleSearch = (e) => {
    e.preventDefault();
    const results = movies.filter(movie =>
      movie.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      movie.director.toLowerCase().includes(searchTerm.toLowerCase()) ||
      movie.year.toString().includes(searchTerm)
    );
    setFilteredMovies(results);
  };

  // Filter movies by genre (from the movie card buttons)
  const handleGenreClick = (genre) => {
    setSelectedGenre(genre);
    const results = movies.filter(movie => movie.genre === genre);
    setFilteredMovies(results);
  };

  const redirectToMovieInfo = (id) => {
    navigate(`/movie/${id}`);
  };

  return (
    <Layout>
      <h1>Search Movies</h1>

      {/* Search Form */}
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search by title, director, or year..."
        />
        <button type="submit">Search</button>
      </form>

      {/* Movie List */}
      <div>
        {filteredMovies.length > 0 ? (
          <ul>
            {filteredMovies.map(movie => (
              <li key={movie.id} style={{ cursor: 'pointer' }}>
                <h3>{movie.title}</h3>
                <p>Director: {movie.director}</p>
                <p>Year: {movie.year}</p>
                <div>
                  <button 
                    onClick={() => handleGenreClick(movie.genre)} 
                    style={{ padding: '10px', cursor: 'pointer', backgroundColor: movie.genre === selectedGenre ? 'lightblue' : 'white' }}
                  >
                    {movie.genre}
                  </button>
                </div>
                <img src={movie.poster} alt={movie.title} style={{ width: '150px', marginTop: '10px' }} />
              </li>
            ))}
          </ul>
        ) : (
          <p>No movies found.</p>
        )}
      </div>
    </Layout>
  );
}

export default Search;
