import { GoogleLogin } from 'react-google-login';
import 'bootstrap/dist/css/bootstrap.min.css';  // Import Bootstrap

const clientId = '723447073505-5ph6b9rvg7h14j21tl0n5cua4dvl8cpa.apps.googleusercontent.com';

function Login() {
  const onSuccess = (res) => {
    console.log('LOGIN SUCCESS! Current User: ', res.profileObj);
  };

  const onFailure = (res) => {
    console.log('LOGIN FAILED! res: ', res);
  };

  return (

      <div className="col-md-6 bg-dark text-center p-4 rounded shadow-sm">
        <h1 className="text-warning mb-4">Login</h1> {/* IMDb-like title */}
        <div id="signInButton" className="d-flex justify-content-center">
          <GoogleLogin
            clientId={clientId}
            buttonText="Login with Google"
            onSuccess={onSuccess}
            onFailure={onFailure}
            cookiePolicy={'single_host_origin'}
            isSignedIn={true}
            className="btn btn-warning"  
          />
        </div>
      </div>

  );
}

export default Login;
