import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout'; // Assuming you have a Layout component

function Logout({ setIsAuthenticated }) {
  const navigate = useNavigate();

  useEffect(() => {
    // Clear any auth tokens and set authentication state to false
    localStorage.removeItem('authToken'); // Clear auth token from localStorage
    setIsAuthenticated(false); // Update the authentication state to false

    // Redirect to home page after 3 seconds
    const timer = setTimeout(() => {
      navigate('/'); // Redirect to home page
    }, 3000); // 3 second delay

    // Cleanup the timer if the component is unmounted
    return () => clearTimeout(timer);
  }, [navigate, setIsAuthenticated]);

  return (
    <Layout>
      <h1>Logging Out...</h1>
      <p>You are being logged out. You will be redirected to the home page shortly.</p>
    </Layout>
  );
}

export default Logout;
