import React, { useState } from 'react';
import Layout from '../components/Layout';  // Importing the Layout

function Dashboard() {
  // Simulated top movies data (could be replaced with API call)
  const [topMovies, setTopMovies] = useState([
    { id: 1, title: 'The Matrix', year: 1999, director: 'The Wachowskis', poster: 'https://example.com/matrix.jpg' },
    { id: 2, title: 'Inception', year: 2010, director: 'Christopher Nolan', poster: 'https://example.com/inception.jpg' },
    { id: 3, title: 'The Dark Knight', year: 2008, director: 'Christopher Nolan', poster: 'https://example.com/darkknight.jpg' },
    { id: 4, title: 'Interstellar', year: 2014, director: 'Christopher Nolan', poster: 'https://example.com/interstellar.jpg' },
    { id: 5, title: 'Pulp Fiction', year: 1994, director: 'Quentin Tarantino', poster: 'https://example.com/pulpfiction.jpg' }
  ]);

  return (
    <Layout>
      <h1>Top Movies</h1>
      <p>Explore the top trending movies this week!</p>

      {/* Section for displaying the top movies */}
      <section style={styles.movieGrid}>
        {topMovies.map(movie => (
          <div key={movie.id} style={styles.movieCard}>
            <img src={movie.poster} alt={movie.title} style={styles.poster} />
            <h3>{movie.title}</h3>
            <p>Year: {movie.year}</p>
            <p>Director: {movie.director}</p>
          </div>
        ))}
      </section>

      {/* Placeholder for recommendations */}
      <section>
        <h2>Recommended for You</h2>
        <p>Personalized movie recommendations based on your watchlist will appear here.</p>
      </section>

      {/* Profile management section */}
      <section>
        <h2>Your Profile</h2>
        <p>Manage your account, update your information, and check your watchlist history.</p>
        <button style={styles.button}>Edit Profile</button>
      </section>
    </Layout>
  );
}

const styles = {
  movieGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '20px',
    padding: '20px 0',
  },
  movieCard: {
    backgroundColor: '#fff',
    padding: '10px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
  },
  poster: {
    width: '100%',
    height: 'auto',
    borderRadius: '4px',
    marginBottom: '10px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
  },
};

export default Dashboard;
