import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import axios from 'axios';
import LoginButton from '../components/login';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  // Fetch the list of users from the API on component mount
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://group16-project2-07b42fe3d45e.herokuapp.com/Users/');
        setUsers(response.data); // Store users in state
      } catch (error) {
        setErrorMessage('Failed to load users. Please try again later.');
        console.error('Error fetching users:', error);
      }
    };
    fetchUsers();
  }, []);

  // Handle form submission for login
  const handleLogin = (e) => {
    e.preventDefault();

    // Find if the username and password match any user from the API
    const foundUser = users.find((user) => user.username === username && user.password === password);

    if (foundUser) {
      navigate('/dashboard'); // Redirect to dashboard on successful login
    } else {
      setErrorMessage('Invalid login credentials');
      setPassword(''); // Clear the password field
    }
  };

  return (
    <Layout>
      <div className="container mt-5 d-flex justify-content-center">
        <div className="col-md-6 bg-dark p-4 rounded shadow-sm">
          <h1 className="text-center mb-4 text-warning">Login</h1>

          {/* Display error message if present */}
          {errorMessage && (
            <div className="alert alert-danger text-center" role="alert">
              {errorMessage}
            </div>
          )}

          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label text-light">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="form-control bg-dark text-white border-light"
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label text-light">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control bg-dark text-white border-light"
                required
              />
            </div>
            <button type="submit" className="btn btn-warning w-100">Login</button>
          </form>
          <div className="mt-3 text-center">
            <LoginButton />
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Login;
